class grupo extends CI_Controller {

	/**
	* Controller action CREATE for controller grupo
	* autogenerated by CASE IGNITER
	*/
	public function create() {

		$this->load->model('usuario_model');
		$data['body']['usuario'] = $this->usuario_model->get_all();

		$this->load->model('imparticion_model');
		$data['body']['imparticion'] = $this->imparticion_model->get_all();

		$this->load->model('curso_model');
		$data['body']['curso'] = $this->curso_model->get_all();

		enmarcar($this, 'grupo/create', $data);
	}

	
	
	/**
	* Controller action CREATE POST for controller grupo
	* autogenerated by CASE IGNITER
	*/
	public function create_post() {
		
		$this->load->model('grupo_model');

		$nombre = ( isset( $_POST['nombre']) ? $_POST['nombre'] : null );
		$grreferencia = ( isset( $_POST['grreferencia']) ? $_POST['grreferencia'] : [] );
		$impartida = ( isset( $_POST['impartida']) ? $_POST['impartida'] : [] );
		$pertenece = ( isset( $_POST['pertenece']) ? $_POST['pertenece'] : [] );

		try {
			$id = $this->grupo_model->create( $nombre, $grreferencia, $impartida, $pertenece );
			$this->list_id($id);
		}
		catch (Exception $e) {
			$data['status'] = 'error';
			$data['message'] = "Error al crear el/la grupo $nombre";
			$this->load->view('grupo/create_message',$data);
		}	
	
	}
				
				
	
	/**
	* Controller action LIST for controller grupo
